mapper.select:
SELECT a.ORDER_NO 'orderNo', a.STAFF_ID 'staffId', a.ORDER_TIME 'orderTime', b.SERVICE_TYPE 'serviceType', a.ORDER_SOURCE 'orderSource', a.ORDER_STATUS 'orderStatus', a.SERVICE_ITEM_CODE 'serviceItemCode', b.MEMBER_NAME 'memberName', b.MEMBER_MOBILE 'memberMobile', b.SERVICE_PROVICE 'serviceProvice', b.SERVICE_CITY 'serviceCity', b.SERVICE_COUNTY 'serviceCounty', a.REMARK 'remarks', c.STAFF_NAME 'staffName', c.AGE 'age', c.ZODIAC 'zodiac', c.NATIVE_PLACE 'nativePlace', c.HEAD_IMAGE 'headImage', a.is_published_job 'isPublishedJob' FROM orders a LEFT JOIN order_customers_info b ON a.ORDER_NO = b.ORDER_NO LEFT JOIN tenants_staffs_info c ON a.STAFF_ID = c.STAFF_ID WHERE a.TENANT_ID = #{tenantId}
AND a.ORDER_NO LIKE concat(concat('%',#{orderNo}),'%')
AND b.MEMBER_NAME LIKE concat(concat('%',#{memberName}),'%')
AND b.MEMBER_MOBILE LIKE concat(concat('%',#{memberMobile}),'%')
AND (a.ORDER_STATUS = #{orderStatus} or a.ORDER_STATUS='06')
AND a.ORDER_STATUS = #{orderStatus}
AND a.ORDER_STATUS in
#{item}
AND a.SERVICE_ITEM_CODE = #{serviceItemCode}
AND b.SERVICE_PROVICE = #{serviceProvince}
AND a.is_published_job = 1 AND a.ORDER_SOURCE = #{orderSource}
AND DATE_format(a.ORDER_TIME,'%Y-%m-%d') >= #{startTime}
AND DATE_format(a.ORDER_TIME,'%Y-%m-%d') <= #{endTime}
AND a.add_account = #{addAccount}
AND a.user_id = #{userId}
ORDER BY a.ORDER_TIME DESC
LIMIT #{pageNum},#{pageSize}

SELECT
count(0)
FROM
orders a LEFT JOIN order_customers_info b ON a.ORDER_NO = b.ORDER_NO
LEFT JOIN tenants_staffs_info c ON a.STAFF_ID = c.STAFF_ID
WHERE
a.TENANT_ID = #{tenantId}

    AND a.ORDER_NO LIKE concat(concat('%',#{orderNo}),'%')


    AND b.MEMBER_NAME LIKE concat(concat('%',#{memberName}),'%')


    AND b.MEMBER_MOBILE LIKE concat(concat('%',#{memberMobile}),'%')


    AND (a.ORDER_STATUS = #{orderStatus} or a.ORDER_STATUS='06')


    AND a.ORDER_STATUS = #{orderStatus}


    AND a.ORDER_STATUS in

        #{item}



    AND a.SERVICE_ITEM_CODE = #{serviceItemCode}


    AND b.SERVICE_PROVICE = #{serviceProvince}



        AND a.is_published_job = 1
        AND a.ORDER_SOURCE = #{orderSource}



    AND DATE_format(a.ORDER_TIME,'%Y-%m-%d') >= #{startTime}


    AND DATE_format(a.ORDER_TIME,'%Y-%m-%d') <= #{endTime}


    AND a.add_account = #{addAccount}


    AND a.user_id = #{userId}
