mapper.select:
select count(0)
from tenants_finance_records
where TENANT_ID =#{tenantId,jdbcType=INTEGER}

select count(0)
from tenants_finance_records
where TENANT_ID =#{tenantId,jdbcType=INTEGER}
and IN_OUT_TYPE='01'

	and DATE_format(add_time,'%Y-%m-%d') >= #{startTime,jdbcType=TIMESTAMP}


	and DATE_format(add_time,'%Y-%m-%d') <= #{endTime,jdbcType=TIMESTAMP}


	and IN_OUT_No=#{inOutNo,jdbcType=VARCHAR}


	and drawee_type=#{draweeType,jdbcType=VARCHAR}

select count(0)
from tenants_finance_records
where TENANT_ID =#{tenantId,jdbcType=INTEGER}
and IN_OUT_TYPE='02'

	and DATE_format(add_time,'%Y-%m-%d') >= #{startTime,jdbcType=TIMESTAMP}


	and DATE_format(add_time,'%Y-%m-%d') <= #{endTime,jdbcType=TIMESTAMP}


	and IN_OUT_No=#{inOutNo,jdbcType=VARCHAR}


	and drawee_type=#{draweeType,jdbcType=VARCHAR}

SELECT
           sum(
			IF (
				IN_OUT_TYPE = '01',
				IN_OUT_AMOUNT,
				0
			)
		) 
       FROM tenants_finance_records 
       where TENANT_ID =#{tenantId,jdbcType=INTEGER}

SELECT
           sum(
			IF (
				IN_OUT_TYPE = '02',
				IN_OUT_AMOUNT,
				0
			)
		) 
       FROM tenants_finance_records 
       where TENANT_ID =#{tenantId,jdbcType=INTEGER}
